import React, {useState} from 'react';
import useWebSocket from 'react-use-websocket';
import printGames from '../src/components/websocketHandler'

import './App.css';

const WS_URL = 'wss://betlivedemo.oservices.me/ws';

function App() {
	console.log(23)
	//give an initial state so that the data won't be undefined at start
	const [bids, setBids] = useState([0]);

	const ws = new WebSocket(WS_URL);
	// endMessage = '{"opt":' + type + ',"evti":' + id + ', "si":' + sport + ', "lng": "' + lang + '", "ski": {{ site_id }}}';
	const apiCall = {
		opt: "1",
		lang: 'en',
		ski: 1
	};

	ws.onopen = (event) => {
		ws.send(JSON.stringify(apiCall));
	};

	ws.onmessage = function (event) {
		const jsonFull = JSON.parse(event.data);
		const json =  JSON.parse(jsonFull.info);
		console.log(json)
		try {
			if ((json.event = "data")) {
				if(jsonFull.opt == 1)
					setBids(json);
			}
		} catch (err) {
			console.log(err);
		}
	};
	//map the first 5 bids
	const firstBids = bids.map((item) => {
		console.log(item)
		printGames(item);
	});

	return <div>{firstBids}</div>;


}

export default  App;
